mapper.update:
UPDATE member
SET
m_name=#{m_name}, m_birth=#{m_birth}, m_sex=#{m_sex},
m_email=#{m_email}
WHERE m_num = #{m_num}

mapper.select:
SELECT COUNT(m_num) as cnt
FROM member
WHERE m_num=#{m_num}
AND m_pw=#{m_pw}

mapper.update:
UPDATE member
SET
m_pw=#{m_pw}
WHERE m_num=#{m_num}

